""" Return the maximum expression.
If any expression is null the return value is database-specific: On PostgreSQL, the maximum not-null expression is returned. On MySQL, Oracle, and SQLite, if any expression is null, null is returned. """ function = 'GREATEST'
def __init__(self, *expressions, **extra): if len(expressions) < 2: raise ValueError('Greatest must take at least two expressions') super().__init__(*expressions, **extra)
def as_sqlite(self, compiler, connection): """Use the MAX function on SQLite.""" return super().as_sqlite(compiler, connection, function='MAX')

""" Return the minimum expression.
If any expression is null the return value is database-specific: On PostgreSQL, return the minimum not-null expression. On MySQL, Oracle, and SQLite, if any expression is null, return null. """ function = 'LEAST'
def __init__(self, *expressions, **extra): if len(expressions) < 2: raise ValueError('Least must take at least two expressions') super().__init__(*expressions, **extra)
def as_sqlite(self, compiler, connection): """Use the MIN function on SQLite.""" return super().as_sqlite(compiler, connection, function='MIN')

