"""Creation fails if the username fails validation."""
user_field = User._meta.get_field(User.USERNAME_FIELD)
new_io = StringIO()
entered_passwords = ['password', 'password']
# Enter an invalid (too long) username first and then a valid one.
invalid_username = ('x' * user_field.max_length) + 'y'
entered_usernames = [invalid_username, 'janet']

def return_passwords():
    return entered_passwords.pop(0)

def return_usernames():
    return entered_usernames.pop(0)

@mock_inputs({'password': return_passwords, 'username': return_usernames, 'email': ''})
def test(self):
    call_command(
        'createsuperuser',
        interactive=True,
        stdin=MockTTY(),
        stdout=new_io,
        stderr=new_io,
    )
    self.assertEqual(
        new_io.getvalue().strip(),
        'Error: Ensure this value has at most %s characters (it has %s).\n'
        'Superuser created successfully.' % (user_field.max_length, len(invalid_username))
    )

test(self)

"""Creation fails if the username already exists."""
user = User.objects.create(username='janet')
new_io = StringIO()
entered_passwords = ['password', 'password']
# Enter the existing username first and then a new one.
entered_usernames = [user.username, 'joe']

def return_passwords():
    return entered_passwords.pop(0)

def return_usernames():
    return entered_usernames.pop(0)

@mock_inputs({'password': return_passwords, 'username': return_usernames, 'email': ''})
def test(self):
    call_command(
        'createsuperuser',
        interactive=True,
        stdin=MockTTY(),
        stdout=new_io,
        stderr=new_io,
    )
    self.assertEqual(
        new_io.getvalue().strip(),
        'Error: That username is already taken.\n'
        'Superuser created successfully.'
    )

test(self)

""" Creation should fail if the user enters mismatched passwords. """ new_io = StringIO()
# The first two passwords do not match, but the second two do match and # are valid. entered_passwords = ["password", "not password", "password2", "password2"]
def mismatched_passwords_then_matched(): return entered_passwords.pop(0)
@mock_inputs({ 'password': mismatched_passwords_then_matched, 'username': 'joe1234567890', 'email': '', }) def test(self): call_command( "createsuperuser", interactive=True, stdin=MockTTY(), stdout=new_io, stderr=new_io, ) self.assertEqual( new_io.getvalue().strip(), "Error: Your passwords didn't match.\n" "Superuser created successfully." )
test(self)

""" Creation should fail if the user enters blank passwords. """ new_io = StringIO()
# The first two passwords are empty strings, but the second two are # valid. entered_passwords = ["", "", "password2", "password2"]
def blank_passwords_then_valid(): return entered_passwords.pop(0)
@mock_inputs({ 'password': blank_passwords_then_valid, 'username': 'joe1234567890', 'email': '', }) def test(self): call_command( "createsuperuser", interactive=True, stdin=MockTTY(), stdout=new_io, stderr=new_io, ) self.assertEqual( new_io.getvalue().strip(), "Error: Blank passwords aren't allowed.\n" "Superuser created successfully." )
test(self)

