self.assertEqual(
    self._run_it({
        'database': 'dbname',
        'user': 'someuser',
        'password': 'somepassword',
        'host': 'somehost',
        'port': '444',
    }), (
        ['psql', '-U', 'someuser', '-h', 'somehost', '-p', '444', 'dbname'],
        'somehost:444:dbname:someuser:somepassword',
    )
)

self.assertEqual(
    self._run_it({
        'database': 'dbname',
        'user': 'someuser',
        'host': 'somehost',
        'port': '444',
    }), (
        ['psql', '-U', 'someuser', '-h', 'somehost', '-p', '444', 'dbname'],
        None,
    )
)

def test_basic(self): now = timezone.now() before = now - timedelta(hours=1) Article.objects.create(title='Testing with Django', written=before, published=now) articles = Article.objects.annotate(last_updated=Greatest('written', 'published')) self.assertEqual(articles.first().last_updated, now)
@skipUnlessDBFeature('greatest_least_ignores_nulls') def test_ignores_null(self): now = timezone.now() Article.objects.create(title='Testing with Django', written=now) articles = Article.objects.annotate(last_updated=Greatest('written', 'published')) self.assertEqual(articles.first().last_updated, now)
@skipIfDBFeature('greatest_least_ignores_nulls') def test_propagates_null(self): Article.objects.create(title='Testing with Django', written=timezone.now()) articles = Article.objects.annotate(last_updated=Greatest('written', 'published')) self.assertIsNone(articles.first().last_updated)
