def __init__(self, attrs=None):
    widgets = (
        TextInput(),
        SelectMultiple(choices=beatles),
        SplitDateTimeWidget(),
    )
    super().__init__(widgets, attrs)

def decompress(self, value):
    if value:
        data = value.split(',')
        return [
            data[0],
            list(data[1]),
            datetime.strptime(data[2], "%Y-%m-%d %H:%M:%S"),
        ]
    return [None, None, None]

