    time.sleep(0.025)
    if lock_status['has_grabbed_lock']:
        return True
return False

    for p1, p2 in zip(country.mpoly, country.scaled):
        for r1, r2 in zip(p1, p2):
            for c1, c2 in zip(r1.coords, r2.coords):
                self.assertAlmostEqual(c1[0] * xfac, c2[0], tol)
                self.assertAlmostEqual(c1[1] * yfac, c2[1], tol)
# Test float/Decimal values

        for p1, p2 in zip(c.mpoly, c.translated):
            for r1, r2 in zip(p1, p2):
                for c1, c2 in zip(r1.coords, r2.coords):
                    # The low precision is for SpatiaLite
                    self.assertAlmostEqual(c1[0] + xfac, c2[0], 5)
                    self.assertAlmostEqual(c1[1] + yfac, c2[1], 5)

# Some combined function tests

class LineStringForm(forms.Form): f = forms.LineStringField()
geom = self.geometries['linestring'] form = LineStringForm(data={'f': geom}) self.assertTextarea(geom, form.as_p()) self.assertMapWidget(form) self.assertFalse(LineStringForm().is_valid())
for invalid in [geo for key, geo in self.geometries.items() if key != 'linestring']: self.assertFalse(LineStringForm(data={'p': invalid.wkt}).is_valid())

class LineStringForm(forms.Form): f = forms.MultiLineStringField()
geom = self.geometries['multilinestring'] form = LineStringForm(data={'f': geom}) self.assertTextarea(geom, form.as_p()) self.assertMapWidget(form) self.assertFalse(LineStringForm().is_valid())
for invalid in [geo for key, geo in self.geometries.items() if key != 'multilinestring']: self.assertFalse(LineStringForm(data={'p': invalid.wkt}).is_valid())

class PolygonForm(forms.Form): p = forms.PolygonField()
geom = self.geometries['polygon'] form = PolygonForm(data={'p': geom}) self.assertTextarea(geom, form.as_p()) self.assertMapWidget(form) self.assertFalse(PolygonForm().is_valid())
for invalid in [geo for key, geo in self.geometries.items() if key != 'polygon']: self.assertFalse(PolygonForm(data={'p': invalid.wkt}).is_valid())

class PolygonForm(forms.Form): p = forms.MultiPolygonField()
geom = self.geometries['multipolygon'] form = PolygonForm(data={'p': geom}) self.assertTextarea(geom, form.as_p()) self.assertMapWidget(form) self.assertFalse(PolygonForm().is_valid())
for invalid in [geo for key, geo in self.geometries.items() if key != 'multipolygon']: self.assertFalse(PolygonForm(data={'p': invalid.wkt}).is_valid())

def __init__(self, get_response):
    self.get_response = get_response

def __call__(self, request):
    return self.get_response(request)

""" TestCase base-class for the URL tests. """
def setUp(self): # Make sure the cache is empty before we are doing our tests. clear_url_caches()
def tearDown(self): # Make sure we will leave an empty cache for other testcases. clear_url_caches()

"""All basic tests for atomic should also pass within an existing transaction."""

def setUp(self):
    self.atomic = transaction.atomic()
    self.atomic.__enter__()

def tearDown(self):
    self.atomic.__exit__(*sys.exc_info())

""" Tests for template rendering when multiple locales are activated during the lifetime of the same process. """
def setUp(self): self._old_language = get_language()
def tearDown(self): activate(self._old_language)

"""All basic tests for atomic should also pass when autocommit is turned off."""
def setUp(self): transaction.set_autocommit(False)
def tearDown(self): # The tests access the database after exercising 'atomic', initiating # a transaction ; a rollback is required before restoring autocommit. transaction.rollback() transaction.set_autocommit(True)

