class Person(models.Model): # Implicit symmetrical=False. friends = models.ManyToManyField('self', through="Relationship")
class Relationship(models.Model): first = models.ForeignKey(Person, models.CASCADE, related_name="rel_from_set") second = models.ForeignKey(Person, models.CASCADE, related_name="rel_to_set")
field = Person._meta.get_field('friends') self.assertEqual(field.check(from_model=Person), [ Error( 'Many-to-many fields with intermediate tables must not be symmetrical.', obj=field, id='fields.E332', ), ])

""" Using through_fields in a m2m with an intermediate model shouldn't mask its incompatibility with symmetry. """ class Person(models.Model): # Explicit symmetrical=True. friends = models.ManyToManyField( 'self', symmetrical=True, through="Relationship", through_fields=('first', 'second'), )
class Relationship(models.Model): first = models.ForeignKey(Person, models.CASCADE, related_name="rel_from_set") second = models.ForeignKey(Person, models.CASCADE, related_name="rel_to_set") referee = models.ForeignKey(Person, models.CASCADE, related_name="referred")
field = Person._meta.get_field('friends') self.assertEqual(field.check(from_model=Person), [ Error( 'Many-to-many fields with intermediate tables must not be symmetrical.', obj=field, id='fields.E332', ), ])

class Person(models.Model): # Explicit symmetrical=True. friends = models.ManyToManyField('self', through="Relationship", symmetrical=True)
class Relationship(models.Model): first = models.ForeignKey(Person, models.CASCADE, related_name="rel_from_set") second = models.ForeignKey(Person, models.CASCADE, related_name="rel_to_set")
field = Person._meta.get_field('friends') self.assertEqual(field.check(from_model=Person), [ Error( 'Many-to-many fields with intermediate tables must not be symmetrical.', obj=field, id='fields.E332', ), ])

class AbstractModel(models.Model):
    class Meta:
        abstract = True

class Model(models.Model):
    rel_string_foreign_key = models.ForeignKey('AbstractModel', models.CASCADE)
    rel_class_foreign_key = models.ForeignKey(AbstractModel, models.CASCADE)

fields = [
    Model._meta.get_field('rel_string_foreign_key'),
    Model._meta.get_field('rel_class_foreign_key'),
]
expected_error = Error(
    "Field defines a relation with model 'AbstractModel', "
    "which is either not installed, or is abstract.",
    id='fields.E300',
)
for field in fields:
    expected_error.obj = field
    self.assertEqual(field.check(), [expected_error])

class AbstractModel(models.Model):
    class Meta:
        abstract = True

class Model(models.Model):
    rel_string_m2m = models.ManyToManyField('AbstractModel')
    rel_class_m2m = models.ManyToManyField(AbstractModel)

fields = [
    Model._meta.get_field('rel_string_m2m'),
    Model._meta.get_field('rel_class_m2m'),
]
expected_error = Error(
    "Field defines a relation with model 'AbstractModel', "
    "which is either not installed, or is abstract.",
    id='fields.E300',
)
for field in fields:
    expected_error.obj = field
    self.assertEqual(field.check(from_model=Model), [expected_error])

class Target(models.Model):
    bad = models.IntegerField()  # No unique=True

class Model(models.Model):
    foreign_key = models.ForeignKey('Target', models.CASCADE, to_field='bad')

field = Model._meta.get_field('foreign_key')
self.assertEqual(field.check(), [
    Error(
        "'Target.bad' must set unique=True because it is referenced by a foreign key.",
        obj=field,
        id='fields.E311',
    ),
])

