class.source:
def remove(self, f):
    """Remove created files"""
    try:
        os.remove(f)
    except:
        # On Windows, attempting to remove a file that is in use
        # causes an exception to be raised
        pass

def u_safe(s):
    try:
        return unicode(s, "unicode_escape")
    except:
        return s

def source_file_def_line(self):
    try:
        return inspect.getsourcelines(self.method_obj)[-1]
    except OSError:
        pass

def __len__(self):
    try:
        return self.sp_index.length
    except:
        return 0

def close(self):
    try:
        self.handle.close()
    except AttributeError:
        pass

def setup(self, op, dtype, axis, use_bottleneck):
    df = pd.DataFrame(np.random.randn(100000, 4)).astype(dtype)
    try:
        pd.options.compute.use_bottleneck = use_bottleneck
    except:
        from pandas.core import nanops
        nanops._USE_BOTTLENECK = use_bottleneck
    self.df_func = getattr(df, op)

def setup(self, op, dtype, use_bottleneck):
    s = pd.Series(np.random.randn(100000)).astype(dtype)
    try:
        pd.options.compute.use_bottleneck = use_bottleneck
    except:
        from pandas.core import nanops
        nanops._USE_BOTTLENECK = use_bottleneck
    self.s_func = getattr(s, op)

def _str_signature(self):
    if self['Signature']:
        return [self['Signature'].replace('*', '\*')] + ['']
    else:
        return ['']

def _str_signature(self):
    return ['']
    if self['Signature']:
        return ['``%s``' % self['Signature']] + ['']
    else:
        return ['']

def _str_summary(self):
    if self['Summary']:
        return self['Summary'] + ['']
    else:
        return []

def _str_extended_summary(self):
    if self['Extended Summary']:
        return self['Extended Summary'] + ['']
    else:
        return []

def __setitem__(self, key, value):
    for mapping in self.maps:
        if key in mapping:
            mapping[key] = value
            return
    self.maps[0][key] = value

def __delitem__(self, key):
    for mapping in self.maps:
        if key in mapping:
            del mapping[key]
            return
    raise KeyError(key)

# override because the m parameter is introduced in Python 3.4

def __delitem__(self, key):
    try:
        del self.maps[0][key]
    except KeyError:
        raise KeyError('Key not found in the first mapping: {!r}'
                       .format(key))

def pop(self, key, *args):
    """
    Remove *key* from maps[0] and return its value. Raise KeyError if
    *key* not in maps[0].
    """
    try:
        return self.maps[0].pop(key, *args)
    except KeyError:
        raise KeyError('Key not found in the first mapping: {!r}'
                       .format(key))

def popitem(self):
    """
    Remove and return an item pair from maps[0]. Raise KeyError is maps[0]
    is empty.
    """
    try:
        return self.maps[0].popitem()
    except KeyError:
        raise KeyError('No keys found in the first mapping.')

def robust_get_level_values(i):
    # if index has labels (that are not None) use those,
    # else use the level location
    try:
        return index.get_level_values(index.names[i])
    except KeyError:
        return index.get_level_values(i)

def validate_window_func(name, args, kwargs):
    numpy_args = ('axis', 'dtype', 'out')
    msg = ("numpy operations are not "
           "valid with window objects. "
           "Use .{func}() directly instead ".format(func=name))

    if len(args) > 0:
        raise UnsupportedFunctionCall(msg)

    for arg in numpy_args:
        if arg in kwargs:
            raise UnsupportedFunctionCall(msg)

def validate_rolling_func(name, args, kwargs):
    numpy_args = ('axis', 'dtype', 'out')
    msg = ("numpy operations are not "
           "valid with window objects. "
           "Use .rolling(...).{func}() instead ".format(func=name))

    if len(args) > 0:
        raise UnsupportedFunctionCall(msg)

    for arg in numpy_args:
        if arg in kwargs:
            raise UnsupportedFunctionCall(msg)

def validate_expanding_func(name, args, kwargs):
    numpy_args = ('axis', 'dtype', 'out')
    msg = ("numpy operations are not "
           "valid with window objects. "
           "Use .expanding(...).{func}() instead ".format(func=name))

    if len(args) > 0:
        raise UnsupportedFunctionCall(msg)

    for arg in numpy_args:
        if arg in kwargs:
            raise UnsupportedFunctionCall(msg)

def _reduce(self, op, name, axis=0, skipna=True, numeric_only=None,
            filter_type=None, **kwds):
    """ perform the reduction type operation """
    func = getattr(self, name, None)
    if func is None:
        msg = 'Categorical cannot perform the operation {op}'
        raise TypeError(msg.format(op=name))
    return func(numeric_only=numeric_only, **kwds)

def _reduce(self, op, name, axis=0, skipna=True, numeric_only=None,
            filter_type=None, **kwds):
    """ perform the reduction type operation if we can """
    func = getattr(self, name, None)
    if func is None:
        raise TypeError("{klass} cannot perform the operation {op}".format(
                        klass=self.__class__.__name__, op=name))
    return func(**kwds)

def time(self):
    """
    Returns numpy array of datetime.time. The time part of the Timestamps.
    """
    # If the Timestamps have a timezone that is not UTC,
    # convert them into their i8 representation while
    # keeping their timezone and not using UTC
    if self.tz is not None and self.tz is not utc:
        timestamps = self._local_timestamps()
    else:
        timestamps = self.asi8

    return tslib.ints_to_pydatetime(timestamps, box="time")

def date(self):
    """
    Returns numpy array of python datetime.date objects (namely, the date
    part of Timestamps without timezone information).
    """
    # If the Timestamps have a timezone that is not UTC,
    # convert them into their i8 representation while
    # keeping their timezone and not using UTC
    if self.tz is not None and self.tz is not utc:
        timestamps = self._local_timestamps()
    else:
        timestamps = self.asi8

    return tslib.ints_to_pydatetime(timestamps, box="date")

def _result_type_many(*arrays_and_dtypes):
    """ wrapper around numpy.result_type which overcomes the NPY_MAXARGS (32)
    argument limit """
    try:
        return np.result_type(*arrays_and_dtypes)
    except ValueError:
        # we have > NPY_MAXARGS terms in our expression
        return reduce(np.result_type, arrays_and_dtypes)

def _get_fill(arr):
    # coerce fill_value to arr dtype if possible
    # int64 SparseArray can have NaN as fill_value if there is no missing
    try:
        return np.asarray(arr.fill_value, dtype=arr.dtype)
    except ValueError:
        return np.asarray(arr.fill_value)

def _parse_date(datestr):
    """ Given a date in xport format, return Python date. """
    try:
        # e.g. "16FEB11:10:07:55"
        return datetime.strptime(datestr, "%d%b%y:%H:%M:%S")
    except ValueError:
        return pd.NaT

def visit_Call_35(self, node, side=None, **kwargs):
    """ in 3.5 the starargs attribute was changed to be more flexible,
    #11097 """

    if isinstance(node.func, ast.Attribute):
        res = self.visit_Attribute(node.func)
    elif not isinstance(node.func, ast.Name):
        raise TypeError("Only named functions are supported")
    else:
        try:
            res = self.visit(node.func)
        except UndefinedVariableError:
            # Check if this is a supported function name
            try:
                res = FuncNode(node.func.id)
            except ValueError:
                # Raise original error
                raise

    if res is None:
        raise ValueError("Invalid function call {func}"
                         .format(func=node.func.id))
    if hasattr(res, 'value'):
        res = res.value

    if isinstance(res, FuncNode):

        new_args = [self.visit(arg) for arg in node.args]

        if node.keywords:
            raise TypeError("Function \"{name}\" does not support keyword "
                            "arguments".format(name=res.name))

        return res(*new_args, **kwargs)

    else:

        new_args = [self.visit(arg).value for arg in node.args]

        for key in node.keywords:
            if not isinstance(key, ast.keyword):
                raise ValueError("keyword error in function call "
                                 "'{func}'".format(func=node.func.id))

            if key.arg:
                # TODO: bug?
                kwargs.append(ast.keyword(
                    keyword.arg, self.visit(keyword.value)))  # noqa

        return self.const_type(res(*new_args, **kwargs), self.env)

def visit_Call_legacy(self, node, side=None, **kwargs):

    # this can happen with: datetime.datetime
    if isinstance(node.func, ast.Attribute):
        res = self.visit_Attribute(node.func)
    elif not isinstance(node.func, ast.Name):
        raise TypeError("Only named functions are supported")
    else:
        try:
            res = self.visit(node.func)
        except UndefinedVariableError:
            # Check if this is a supported function name
            try:
                res = FuncNode(node.func.id)
            except ValueError:
                # Raise original error
                raise

    if res is None:
        raise ValueError("Invalid function call {func}"
                         .format(func=node.func.id))
    if hasattr(res, 'value'):
        res = res.value

    if isinstance(res, FuncNode):
        args = [self.visit(targ) for targ in node.args]

        if node.starargs is not None:
            args += self.visit(node.starargs)

        if node.keywords or node.kwargs:
            raise TypeError("Function \"{name}\" does not support keyword "
                            "arguments".format(name=res.name))

        return res(*args, **kwargs)

    else:
        args = [self.visit(targ).value for targ in node.args]
        if node.starargs is not None:
            args += self.visit(node.starargs).value

        keywords = {}
        for key in node.keywords:
            if not isinstance(key, ast.keyword):
                raise ValueError("keyword error in function call "
                                 "'{func}'".format(func=node.func.id))
            keywords[key.arg] = self.visit(key.value).value
        if node.kwargs is not None:
            keywords.update(self.visit(node.kwargs).value)

        return self.const_type(res(*args, **keywords), self.env)

def is_datetime(self):
    try:
        t = self.type.type
    except AttributeError:
        t = self.type

    return issubclass(t, (datetime, np.datetime64))

def is_datetime(self):
    try:
        t = self.return_type.type
    except AttributeError:
        t = self.return_type

    return issubclass(t, (datetime, np.datetime64))

def _in(x, y):
    """Compute the vectorized membership of ``x in y`` if possible, otherwise
    use Python.
    """
    try:
        return x.isin(y)
    except AttributeError:
        if is_list_like(x):
            try:
                return y.isin(x)
            except AttributeError:
                pass
        return x in y

def _not_in(x, y):
    """Compute the vectorized membership of ``x not in y`` if possible,
    otherwise use Python.
    """
    try:
        return ~x.isin(y)
    except AttributeError:
        if is_list_like(x):
            try:
                return ~y.isin(x)
            except AttributeError:
                pass
        return x not in y

def stringify(value):
    if self.encoding is not None:
        encoder = partial(pprint_thing_encoded,
                          encoding=self.encoding)
    else:
        encoder = pprint_thing
    return encoder(value)

def stringify(value):
    if self.encoding is not None:
        encoder = partial(pprint_thing_encoded,
                          encoding=self.encoding)
    else:
        encoder = pprint_thing
    return encoder(value)

def _isna_new(obj):
    if is_scalar(obj):
        return libmissing.checknull(obj)
    # hack (for now) because MI registers as ndarray
    elif isinstance(obj, ABCMultiIndex):
        raise NotImplementedError("isna is not defined for MultiIndex")
    elif isinstance(obj, (ABCSeries, np.ndarray, ABCIndexClass,
                          ABCExtensionArray)):
        return _isna_ndarraylike(obj)
    elif isinstance(obj, ABCGeneric):
        return obj._constructor(obj._data.isna(func=isna))
    elif isinstance(obj, list):
        return _isna_ndarraylike(np.asarray(obj, dtype=object))
    elif hasattr(obj, '__array__'):
        return _isna_ndarraylike(np.asarray(obj))
    else:
        return obj is None

def _isna_old(obj):
    """Detect missing values. Treat None, NaN, INF, -INF as null.

    Parameters
    ----------
    arr: ndarray or object value

    Returns
    -------
    boolean ndarray or boolean
    """
    if is_scalar(obj):
        return libmissing.checknull_old(obj)
    # hack (for now) because MI registers as ndarray
    elif isinstance(obj, ABCMultiIndex):
        raise NotImplementedError("isna is not defined for MultiIndex")
    elif isinstance(obj, (ABCSeries, np.ndarray, ABCIndexClass)):
        return _isna_ndarraylike_old(obj)
    elif isinstance(obj, ABCGeneric):
        return obj._constructor(obj._data.isna(func=_isna_old))
    elif isinstance(obj, list):
        return _isna_ndarraylike_old(np.asarray(obj, dtype=object))
    elif hasattr(obj, '__array__'):
        return _isna_ndarraylike_old(np.asarray(obj))
    else:
        return obj is None

def _aggregate(self, result, counts, values, comp_ids, agg_func,
               is_numeric, is_datetimelike, min_count=-1):
    if values.ndim > 3:
        # punting for now
        raise NotImplementedError("number of dimensions is currently "
                                  "limited to 3")
    elif values.ndim > 2:
        for i, chunk in enumerate(values.transpose(2, 0, 1)):

            chunk = chunk.squeeze()
            agg_func(result[:, :, i], counts, chunk, comp_ids,
                     min_count)
    else:
        agg_func(result, counts, values, comp_ids, min_count)

    return result

def _transform(self, result, values, comp_ids, transform_func,
               is_numeric, is_datetimelike, **kwargs):

    comp_ids, _, ngroups = self.group_info
    if values.ndim > 3:
        # punting for now
        raise NotImplementedError("number of dimensions is currently "
                                  "limited to 3")
    elif values.ndim > 2:
        for i, chunk in enumerate(values.transpose(2, 0, 1)):

            transform_func(result[:, :, i], values,
                           comp_ids, is_datetimelike, **kwargs)
    else:
        transform_func(result, values, comp_ids, is_datetimelike, **kwargs)

    return result

def agg_series(self, obj, func):
    try:
        return self._aggregate_series_fast(obj, func)
    except Exception:
        return self._aggregate_series_pure_python(obj, func)

def get_label(i):
    try:
        return pprint_thing(data.index[i])
    except Exception:
        return ''

def P1(a):
    try:
        return np.percentile(a.dropna(), q=1)
    except Exception:
        return np.nan

def min(self, axis=None, *args, **kwargs):
    """
    Return the minimum value of the Index or minimum along
    an axis.

    See also
    --------
    numpy.ndarray.min
    """
    nv.validate_min(args, kwargs)

    try:
        i8 = self.asi8

        # quick check
        if len(i8) and self.is_monotonic:
            if i8[0] != iNaT:
                return self._box_func(i8[0])

        if self.hasnans:
            min_stamp = self[~self._isnan].asi8.min()
        else:
            min_stamp = i8.min()
        return self._box_func(min_stamp)
    except ValueError:
        return self._na_value

def max(self, axis=None, *args, **kwargs):
    """
    Return the maximum value of the Index or maximum along
    an axis.

    See also
    --------
    numpy.ndarray.max
    """
    nv.validate_max(args, kwargs)

    try:
        i8 = self.asi8

        # quick check
        if len(i8) and self.is_monotonic:
            if i8[-1] != iNaT:
                return self._box_func(i8[-1])

        if self.hasnans:
            max_stamp = self[~self._isnan].asi8.max()
        else:
            max_stamp = i8.max()
        return self._box_func(max_stamp)
    except ValueError:
        return self._na_value

def _dt_index_cmp(cls, op):
    """
    Wrap comparison operations to convert datetime-like to datetime64
    """
    opname = '__{name}__'.format(name=op.__name__)

    def wrapper(self, other):
        result = getattr(DatetimeArrayMixin, opname)(self, other)
        if is_bool_dtype(result):
            return result
        return Index(result)

    return compat.set_function_name(wrapper, opname, cls)

def _td_index_cmp(cls, op):
    """
    Wrap comparison operations to convert timedelta-like to timedelta64
    """
    opname = '__{name}__'.format(name=op.__name__)

    def wrapper(self, other):
        result = getattr(TimedeltaArrayMixin, opname)(self, other)
        if is_bool_dtype(result):
            # support of bool dtype indexers
            return result
        return Index(result)

    return compat.set_function_name(wrapper, opname, cls)

def union(self, other):
    """
    Specialized union for TimedeltaIndex objects. If combine
    overlapping ranges with the same DateOffset, will be much
    faster than Index.union

    Parameters
    ----------
    other : TimedeltaIndex or array-like

    Returns
    -------
    y : Index or TimedeltaIndex
    """
    self._assert_can_do_setop(other)
    if not isinstance(other, TimedeltaIndex):
        try:
            other = TimedeltaIndex(other)
        except (TypeError, ValueError):
            pass
    this, other = self, other

    if this._can_fast_union(other):
        return this._fast_union(other)
    else:
        result = Index.union(this, other)
        if isinstance(result, TimedeltaIndex):
            if result.freq is None:
                result.freq = to_offset(result.inferred_freq)
        return result

def _can_fast_union(self, other):
    if not isinstance(other, DatetimeIndex):
        return False

    freq = self.freq

    if freq is None or freq != other.freq:
        return False

    if not self.is_monotonic or not other.is_monotonic:
        return False

    if len(self) == 0 or len(other) == 0:
        return True

    # to make our life easier, "sort" the two ranges
    if self[0] <= other[0]:
        left, right = self, other
    else:
        left, right = other, self

    right_start = right[0]
    left_end = left[-1]

    # Only need to "adjoin", not overlap
    try:
        return (right_start == left_end + freq) or right_start in left
    except (ValueError):

        # if we are comparing a freq that does not propagate timezones
        # this will raise
        return False

def _can_fast_union(self, other):
    if not isinstance(other, TimedeltaIndex):
        return False

    freq = self.freq

    if freq is None or freq != other.freq:
        return False

    if not self.is_monotonic or not other.is_monotonic:
        return False

    if len(self) == 0 or len(other) == 0:
        return True

    # to make our life easier, "sort" the two ranges
    if self[0] <= other[0]:
        left, right = self, other
    else:
        left, right = other, self

    right_start = right[0]
    left_end = left[-1]

    # Only need to "adjoin", not overlap
    return (right_start == left_end + freq) or right_start in left

def _fast_union(self, other):
    if len(other) == 0:
        return self.view(type(self))

    if len(self) == 0:
        return other.view(type(self))

    # to make our life easier, "sort" the two ranges
    if self[0] <= other[0]:
        left, right = self, other
    else:
        left, right = other, self

    left_start, left_end = left[0], left[-1]
    right_end = right[-1]

    if not self.freq._should_cache():
        # concatenate dates
        if left_end < right_end:
            loc = right.searchsorted(left_end, side='right')
            right_chunk = right.values[loc:]
            dates = _concat._concat_compat((left.values, right_chunk))
            return self._shallow_copy(dates)
        else:
            return left
    else:
        return type(self)(start=left_start,
                          end=max(left_end, right_end),
                          freq=left.freq)

def _fast_union(self, other):
    if len(other) == 0:
        return self.view(type(self))

    if len(self) == 0:
        return other.view(type(self))

    # to make our life easier, "sort" the two ranges
    if self[0] <= other[0]:
        left, right = self, other
    else:
        left, right = other, self

    left_end = left[-1]
    right_end = right[-1]

    # concatenate
    if left_end < right_end:
        loc = right.searchsorted(left_end, side='right')
        right_chunk = right.values[loc:]
        dates = _concat._concat_compat((left.values, right_chunk))
        return self._shallow_copy(dates)
    else:
        return left

def get_value(self, series, key):
    """
    Fast lookup of value from 1-dimensional ndarray. Only use this if you
    know what you're doing
    """

    if isinstance(key, datetime):

        # needed to localize naive datetimes
        if self.tz is not None:
            key = Timestamp(key, tz=self.tz)

        return self.get_value_maybe_box(series, key)

    if isinstance(key, time):
        locs = self.indexer_at_time(key)
        return series.take(locs)

    try:
        return com.maybe_box(self, Index.get_value(self, series, key),
                             series, key)
    except KeyError:
        try:
            loc = self._get_string_slice(key)
            return series[loc]
        except (TypeError, ValueError, KeyError):
            pass

        try:
            return self.get_value_maybe_box(series, key)
        except (TypeError, ValueError, KeyError):
            raise KeyError(key)

def get_value(self, series, key):
    """
    Fast lookup of value from 1-dimensional ndarray. Only use this if you
    know what you're doing
    """

    if _is_convertible_to_td(key):
        key = Timedelta(key)
        return self.get_value_maybe_box(series, key)

    try:
        return com.maybe_box(self, Index.get_value(self, series, key),
                             series, key)
    except KeyError:
        try:
            loc = self._get_string_slice(key)
            return series[loc]
        except (TypeError, ValueError, KeyError):
            pass

        try:
            return self.get_value_maybe_box(series, key)
        except (TypeError, ValueError, KeyError):
            raise KeyError(key)

def searchsorted(self, value, side='left', sorter=None):
    if isinstance(value, (np.ndarray, Index)):
        value = np.array(value, dtype=_NS_DTYPE, copy=False)
    else:
        value = _to_m8(value, tz=self.tz)

    return self.values.searchsorted(value, side=side)

def searchsorted(self, value, side='left', sorter=None):
    if isinstance(value, (np.ndarray, Index)):
        value = np.array(value, dtype=_TD_DTYPE, copy=False)
    else:
        value = _to_m8(value)

    return self.values.searchsorted(value, side=side, sorter=sorter)

def delete(self, loc):
    """
    Make a new DatetimeIndex with passed location(s) deleted.

    Parameters
    ----------
    loc: int, slice or array of ints
        Indicate which sub-arrays to remove.

    Returns
    -------
    new_index : DatetimeIndex
    """
    new_dates = np.delete(self.asi8, loc)

    freq = None
    if is_integer(loc):
        if loc in (0, -len(self), -1, len(self) - 1):
            freq = self.freq
    else:
        if is_list_like(loc):
            loc = lib.maybe_indices_to_slice(
                ensure_int64(np.array(loc)), len(self))
        if isinstance(loc, slice) and loc.step in (1, None):
            if (loc.start in (0, None) or loc.stop in (len(self), None)):
                freq = self.freq

    return DatetimeIndex(new_dates, name=self.name, freq=freq, tz=self.tz)

def delete(self, loc):
    """
    Make a new TimedeltaIndex with passed location(s) deleted.

    Parameters
    ----------
    loc: int, slice or array of ints
        Indicate which sub-arrays to remove.

    Returns
    -------
    new_index : TimedeltaIndex
    """
    new_tds = np.delete(self.asi8, loc)

    freq = 'infer'
    if is_integer(loc):
        if loc in (0, -len(self), -1, len(self) - 1):
            freq = self.freq
    else:
        if is_list_like(loc):
            loc = lib.maybe_indices_to_slice(
                ensure_int64(np.array(loc)), len(self))
        if isinstance(loc, slice) and loc.step in (1, None):
            if (loc.start in (0, None) or loc.stop in (len(self), None)):
                freq = self.freq

    return TimedeltaIndex(new_tds, name=self.name, freq=freq)

def _convert_scalar_indexer(self, key, kind=None):
    assert kind in ['ix', 'loc', 'getitem', 'iloc', None]

    # don't coerce ilocs to integers
    if kind != 'iloc':
        key = self._maybe_cast_indexer(key)
    return (super(Int64Index, self)
            ._convert_scalar_indexer(key, kind=kind))

def _convert_scalar_indexer(self, key, kind=None):
    assert kind in ['ix', 'loc', 'getitem', 'iloc', None]

    # don't coerce ilocs to integers
    if kind != 'iloc':
        key = self._maybe_cast_indexer(key)
    return (super(UInt64Index, self)
            ._convert_scalar_indexer(key, kind=kind))

def _assert_safe_casting(cls, data, subarr):
    """
    Ensure incoming data can be represented as ints.
    """
    if not issubclass(data.dtype.type, np.signedinteger):
        if not np.array_equal(data, subarr):
            raise TypeError('Unsafe NumPy casting, you must '
                            'explicitly cast')

def _assert_safe_casting(cls, data, subarr):
    """
    Ensure incoming data can be represented as uints.
    """
    if not issubclass(data.dtype.type, np.unsignedinteger):
        if not np.array_equal(data, subarr):
            raise TypeError('Unsafe NumPy casting, you must '
                            'explicitly cast')

def pad_1d(values, limit=None, mask=None, dtype=None):
    if dtype is None:
        dtype = values.dtype
    _method = None
    if is_float_dtype(values):
        name = 'pad_inplace_{name}'.format(name=dtype.name)
        _method = getattr(algos, name, None)
    elif is_datetime64_dtype(dtype) or is_datetime64tz_dtype(dtype):
        _method = _pad_1d_datetime
    elif is_integer_dtype(values):
        values = ensure_float64(values)
        _method = algos.pad_inplace_float64
    elif values.dtype == np.object_:
        _method = algos.pad_inplace_object

    if _method is None:
        raise ValueError('Invalid dtype for pad_1d [{name}]'
                         .format(name=dtype.name))

    if mask is None:
        mask = isna(values)
    mask = mask.view(np.uint8)
    _method(values, mask, limit=limit)
    return values

def backfill_1d(values, limit=None, mask=None, dtype=None):
    if dtype is None:
        dtype = values.dtype
    _method = None
    if is_float_dtype(values):
        name = 'backfill_inplace_{name}'.format(name=dtype.name)
        _method = getattr(algos, name, None)
    elif is_datetime64_dtype(dtype) or is_datetime64tz_dtype(dtype):
        _method = _backfill_1d_datetime
    elif is_integer_dtype(values):
        values = ensure_float64(values)
        _method = algos.backfill_inplace_float64
    elif values.dtype == np.object_:
        _method = algos.backfill_inplace_object

    if _method is None:
        raise ValueError('Invalid dtype for backfill_1d [{name}]'
                         .format(name=dtype.name))

    if mask is None:
        mask = isna(values)
    mask = mask.view(np.uint8)

    _method(values, mask, limit=limit)
    return values

def backfill_2d(values, limit=None, mask=None, dtype=None):
    if dtype is None:
        dtype = values.dtype
    _method = None
    if is_float_dtype(values):
        name = 'backfill_2d_inplace_{name}'.format(name=dtype.name)
        _method = getattr(algos, name, None)
    elif is_datetime64_dtype(dtype) or is_datetime64tz_dtype(dtype):
        _method = _backfill_2d_datetime
    elif is_integer_dtype(values):
        values = ensure_float64(values)
        _method = algos.backfill_2d_inplace_float64
    elif values.dtype == np.object_:
        _method = algos.backfill_2d_inplace_object

    if _method is None:
        raise ValueError('Invalid dtype for backfill_2d [{name}]'
                         .format(name=dtype.name))

    if mask is None:
        mask = isna(values)
    mask = mask.view(np.uint8)

    if np.all(values.shape):
        _method(values, mask, limit=limit)
    else:
        # for test coverage
        pass
    return values

def pad_2d(values, limit=None, mask=None, dtype=None):
    if dtype is None:
        dtype = values.dtype
    _method = None
    if is_float_dtype(values):
        name = 'pad_2d_inplace_{name}'.format(name=dtype.name)
        _method = getattr(algos, name, None)
    elif is_datetime64_dtype(dtype) or is_datetime64tz_dtype(dtype):
        _method = _pad_2d_datetime
    elif is_integer_dtype(values):
        values = ensure_float64(values)
        _method = algos.pad_2d_inplace_float64
    elif values.dtype == np.object_:
        _method = algos.pad_2d_inplace_object

    if _method is None:
        raise ValueError('Invalid dtype for pad_2d [{name}]'
                         .format(name=dtype.name))

    if mask is None:
        mask = isna(values)
    mask = mask.view(np.uint8)

    if np.all(values.shape):
        _method(values, mask, limit=limit)
    else:
        # for test coverage
        pass
    return values

def nancorr(a, b, method='pearson', min_periods=None):
    """
    a, b: ndarrays
    """
    if len(a) != len(b):
        raise AssertionError('Operands to nancorr must have same size')

    if min_periods is None:
        min_periods = 1

    valid = notna(a) & notna(b)
    if not valid.all():
        a = a[valid]
        b = b[valid]

    if len(a) < min_periods:
        return np.nan

    f = get_corr_func(method)
    return f(a, b)

def nancov(a, b, min_periods=None):
    if len(a) != len(b):
        raise AssertionError('Operands to nancov must have same size')

    if min_periods is None:
        min_periods = 1

    valid = notna(a) & notna(b)
    if not valid.all():
        a = a[valid]
        b = b[valid]

    if len(a) < min_periods:
        return np.nan

    return np.cov(a, b)[0, 1]

def kind(self):
    if isinstance(self.sp_index, BlockIndex):
        return 'block'
    elif isinstance(self.sp_index, IntIndex):
        return 'integer'

def kind(self):
    if isinstance(self.sp_index, BlockIndex):
        return 'block'
    elif isinstance(self.sp_index, IntIndex):
        return 'integer'

def __array_wrap__(self, result, context=None): """ Gets called prior to a ufunc (and after)
See SparseArray.__array_wrap__ for detail. """ if isinstance(context, tuple) and len(context) == 3: ufunc, args, domain = context args = [getattr(a, 'fill_value', a) for a in args] with np.errstate(all='ignore'): fill_value = ufunc(self.fill_value, *args[1:]) else: fill_value = self.fill_value
return self._constructor(result, index=self.index, sparse_index=self.sp_index, fill_value=fill_value, copy=False).__finalize__(self)

def __getslice__(self, i, j):
    if i < 0:
        i = 0
    if j < 0:
        j = 0
    slobj = slice(i, j)
    return self.__getitem__(slobj)

def _check_func(minp, window):
    if minp is None:
        return window
    else:
        return max(p, minp)

def _use_window(minp, window):
    if minp is None:
        return window
    else:
        return minp

def read_excel(io, sheet_name=0, header=0, names=None, index_col=None, usecols=None, squeeze=False, dtype=None, engine=None, converters=None, true_values=None, false_values=None, skiprows=None, nrows=None, na_values=None, parse_dates=False, date_parser=None, thousands=None, comment=None, skipfooter=0, convert_float=True, **kwds):
# Can't use _deprecate_kwarg since sheetname=None has a special meaning if is_integer(sheet_name) and sheet_name == 0 and 'sheetname' in kwds: warnings.warn("The `sheetname` keyword is deprecated, use " "`sheet_name` instead", FutureWarning, stacklevel=2) sheet_name = kwds.pop("sheetname")
if 'sheet' in kwds: raise TypeError("read_excel() got an unexpected keyword argument " "`sheet`")
if not isinstance(io, ExcelFile): io = ExcelFile(io, engine=engine)
return io.parse( sheet_name=sheet_name, header=header, names=names, index_col=index_col, usecols=usecols, squeeze=squeeze, dtype=dtype, converters=converters, true_values=true_values, false_values=false_values, skiprows=skiprows, nrows=nrows, na_values=na_values, parse_dates=parse_dates, date_parser=date_parser, thousands=thousands, comment=comment, skipfooter=skipfooter, convert_float=convert_float, **kwds)

def write_cells(self, cells, sheet_name=None, startrow=0, startcol=0,
                freeze_panes=None):
    # Write the frame cells using xlwt.

    sheet_name = self._get_sheet_name(sheet_name)

    if sheet_name in self.sheets:
        wks = self.sheets[sheet_name]
    else:
        wks = self.book.add_sheet(sheet_name)
        self.sheets[sheet_name] = wks

    if _validate_freeze_panes(freeze_panes):
        wks.set_panes_frozen(True)
        wks.set_horz_split_pos(freeze_panes[0])
        wks.set_vert_split_pos(freeze_panes[1])

    style_dict = {}

    for cell in cells:
        val, fmt = self._value_with_fmt(cell.val)

        stylekey = json.dumps(cell.style)
        if fmt:
            stylekey += fmt

        if stylekey in style_dict:
            style = style_dict[stylekey]
        else:
            style = self._convert_to_style(cell.style, fmt)
            style_dict[stylekey] = style

        if cell.mergestart is not None and cell.mergeend is not None:
            wks.write_merge(startrow + cell.row,
                            startrow + cell.mergestart,
                            startcol + cell.col,
                            startcol + cell.mergeend,
                            val, style)
        else:
            wks.write(startrow + cell.row,
                      startcol + cell.col,
                      val, style)

def write_cells(self, cells, sheet_name=None, startrow=0, startcol=0,
                freeze_panes=None):
    # Write the frame cells using xlsxwriter.
    sheet_name = self._get_sheet_name(sheet_name)

    if sheet_name in self.sheets:
        wks = self.sheets[sheet_name]
    else:
        wks = self.book.add_worksheet(sheet_name)
        self.sheets[sheet_name] = wks

    style_dict = {'null': None}

    if _validate_freeze_panes(freeze_panes):
        wks.freeze_panes(*(freeze_panes))

    for cell in cells:
        val, fmt = self._value_with_fmt(cell.val)

        stylekey = json.dumps(cell.style)
        if fmt:
            stylekey += fmt

        if stylekey in style_dict:
            style = style_dict[stylekey]
        else:
            style = self.book.add_format(
                _XlsxStyler.convert(cell.style, fmt))
            style_dict[stylekey] = style

        if cell.mergestart is not None and cell.mergeend is not None:
            wks.merge_range(startrow + cell.row,
                            startcol + cell.col,
                            startrow + cell.mergestart,
                            startcol + cell.mergeend,
                            cell.val, style)
        else:
            wks.write(startrow + cell.row,
                      startcol + cell.col,
                      val, style)

def _get_axes_layout(self):
    axes = self._get_subplots()
    x_set = set()
    y_set = set()
    for ax in axes:
        # check axes coordinates to estimate layout
        points = ax.get_position().get_points()
        x_set.add(points[0][0])
        y_set.add(points[0][1])
    return (len(y_set), len(x_set))

def _get_axes_layout(self, axes):
    x_set = set()
    y_set = set()
    for ax in axes:
        # check axes coordinates to estimate layout
        points = ax.get_position().get_points()
        x_set.add(points[0][0])
        y_set.add(points[0][1])
    return (len(y_set), len(x_set))

def orientation(self):
    if self.kwds.get('orientation', None) == 'horizontal':
        return 'horizontal'
    else:
        return 'vertical'

def orientation(self):
    if self.kwds.get('vert', True):
        return 'vertical'
    else:
        return 'horizontal'

def test_dti_cmp_nat(self): left = pd.DatetimeIndex([pd.Timestamp('2011-01-01'), pd.NaT, pd.Timestamp('2011-01-03')]) right = pd.DatetimeIndex([pd.NaT, pd.NaT, pd.Timestamp('2011-01-03')])
for lhs, rhs in [(left, right), (left.astype(object), right.astype(object))]: result = rhs == lhs expected = np.array([False, False, True]) tm.assert_numpy_array_equal(result, expected)
result = lhs != rhs expected = np.array([True, True, False]) tm.assert_numpy_array_equal(result, expected)
expected = np.array([False, False, False]) tm.assert_numpy_array_equal(lhs == pd.NaT, expected) tm.assert_numpy_array_equal(pd.NaT == rhs, expected)
expected = np.array([True, True, True]) tm.assert_numpy_array_equal(lhs != pd.NaT, expected) tm.assert_numpy_array_equal(pd.NaT != lhs, expected)
expected = np.array([False, False, False]) tm.assert_numpy_array_equal(lhs < pd.NaT, expected) tm.assert_numpy_array_equal(pd.NaT > lhs, expected)

