# only test dt with dt, otherwise weird joins result

def test_bool_ops_with_constants(self):
    for op, lhs, rhs in product(expr._bool_ops_syms, ('True', 'False'),
                                ('True', 'False')):
        ex = '{0} {1} {2}'.format(lhs, op, rhs)
        if op in ('and', 'or'):
            with pytest.raises(NotImplementedError):
                self.eval(ex)
        else:
            res = self.eval(ex)
            exp = eval(ex)
            assert res == exp

def test_simple_bool_ops(self):
    for op, lhs, rhs in product(expr._bool_ops_syms, (True, False),
                                (True, False)):
        ex = 'lhs {0} rhs'.format(op)
        if op in ('and', 'or'):
            with pytest.raises(NotImplementedError):
                pd.eval(ex, engine=self.engine, parser=self.parser)
        else:
            res = pd.eval(ex, engine=self.engine, parser=self.parser)
            exp = eval(ex)
            assert res == exp

def test_validate_bool_args(self):
    invalid_values = [1, "True", [1, 2, 3], 5.0]

    for value in invalid_values:
        with pytest.raises(ValueError):
            pd.eval("2+2", inplace=value)

def test_validate_bool_args(self):
    invalid_values = [1, "True", [1, 2, 3], 5.0]

    for value in invalid_values:
        with pytest.raises(ValueError):
            self.int_series.drop_duplicates(inplace=value)

def test_value_counts(self, all_data, dropna):
    all_data = all_data[:10]
    if dropna:
        other = np.array(all_data[~all_data.isna()])
    else:
        other = all_data

    result = pd.Series(all_data).value_counts(dropna=dropna).sort_index()
    expected = pd.Series(other).value_counts(
        dropna=dropna).sort_index()

    self.assert_series_equal(result, expected)

def test_value_counts(self, all_data, dropna):
    all_data = all_data[:10]
    if dropna:
        other = np.array(all_data[~all_data.isna()])
    else:
        other = all_data

    result = pd.Series(all_data).value_counts(dropna=dropna).sort_index()
    expected = pd.Series(other).value_counts(dropna=dropna).sort_index()

    tm.assert_series_equal(result, expected)

def data_repeated():
    """Return different versions of data for count times"""
    def gen(count):
        for _ in range(count):
            yield Categorical(make_data())
    yield gen

def data_repeated(data):
    def gen(count):
        for _ in range(count):
            yield data
    yield gen

def data_repeated():
    """Return different versions of data for count times"""
    def gen(count):
        for _ in range(count):
            yield IntervalArray(make_data())
    yield gen

def data_repeated():
    def gen(count):
        for _ in range(count):
            yield DecimalArray(make_data())
    yield gen

def data_repeated():
    """Return different versions of data for count times"""
    def gen(count):
        for _ in range(count):
            yield NotImplementedError
    yield gen

