def test_union(self, tz): rng1 = pd.date_range('1/1/2000', freq='D', periods=5, tz=tz) other1 = pd.date_range('1/6/2000', freq='D', periods=5, tz=tz) expected1 = pd.date_range('1/1/2000', freq='D', periods=10, tz=tz)
rng2 = pd.date_range('1/1/2000', freq='D', periods=5, tz=tz) other2 = pd.date_range('1/4/2000', freq='D', periods=5, tz=tz) expected2 = pd.date_range('1/1/2000', freq='D', periods=8, tz=tz)
rng3 = pd.date_range('1/1/2000', freq='D', periods=5, tz=tz) other3 = pd.DatetimeIndex([], tz=tz) expected3 = pd.date_range('1/1/2000', freq='D', periods=5, tz=tz)
for rng, other, expected in [(rng1, other1, expected1), (rng2, other2, expected2), (rng3, other3, expected3)]:
result_union = rng.union(other) tm.assert_index_equal(result_union, expected)

def test_difference(self, tz): rng1 = pd.date_range('1/1/2000', freq='D', periods=5, tz=tz) other1 = pd.date_range('1/6/2000', freq='D', periods=5, tz=tz) expected1 = pd.date_range('1/1/2000', freq='D', periods=5, tz=tz)
rng2 = pd.date_range('1/1/2000', freq='D', periods=5, tz=tz) other2 = pd.date_range('1/4/2000', freq='D', periods=5, tz=tz) expected2 = pd.date_range('1/1/2000', freq='D', periods=3, tz=tz)
rng3 = pd.date_range('1/1/2000', freq='D', periods=5, tz=tz) other3 = pd.DatetimeIndex([], tz=tz) expected3 = pd.date_range('1/1/2000', freq='D', periods=5, tz=tz)
for rng, other, expected in [(rng1, other1, expected1), (rng2, other2, expected2), (rng3, other3, expected3)]: result_diff = rng.difference(other) tm.assert_index_equal(result_diff, expected)

def test_union_coverage(self):
idx = TimedeltaIndex(['3d', '1d', '2d']) ordered = TimedeltaIndex(idx.sort_values(), freq='infer') result = ordered.union(idx) tm.assert_index_equal(result, ordered)
result = ordered[:0].union(ordered) tm.assert_index_equal(result, ordered) assert result.freq == ordered.freq

def get_kwargs_from_breaks(self, breaks, closed='right'):
    """
    converts intervals in breaks format to a dictionary of kwargs to
    specific to the format expected by IntervalIndex.from_tuples
    """
    if len(breaks) == 0:
        return {'data': breaks}

    tuples = lzip(breaks[:-1], breaks[1:])
    if isinstance(breaks, (list, tuple)):
        return {'data': tuples}
    elif is_categorical_dtype(breaks):
        return {'data': breaks._constructor(tuples)}
    return {'data': com.asarray_tuplesafe(tuples)}

def get_kwargs_from_breaks(self, breaks, closed='right'):
    """
    converts intervals in breaks format to a dictionary of kwargs to
    specific to the format expected by the IntervalIndex/Index constructors
    """
    if len(breaks) == 0:
        return {'data': breaks}

    ivs = [Interval(l, r, closed) if notna(l) else l
           for l, r in zip(breaks[:-1], breaks[1:])]

    if isinstance(breaks, list):
        return {'data': ivs}
    elif is_categorical_dtype(breaks):
        return {'data': breaks._constructor(ivs)}
    return {'data': np.array(ivs, dtype=object)}

def test_unicode_string_with_unicode():
    d = {"a": [u("\u05d0"), 2, 3], "b": [4, 5, 6], "c": [7, 8, 9]}
    idx = pd.DataFrame(d).set_index(["a", "b"]).index

    if PY3:
        str(idx)
    else:
        compat.text_type(idx)

def test_bytestring_with_unicode():
    d = {"a": [u("\u05d0"), 2, 3], "b": [4, 5, 6], "c": [7, 8, 9]}
    idx = pd.DataFrame(d).set_index(["a", "b"]).index

    if PY3:
        bytes(idx)
    else:
        str(idx)

def test_coercion_with_setitem(self):
    for start_data, expected_result in self.EXPECTED_RESULTS:
        start_series = Series(start_data)
        start_series[0] = None

        expected_series = Series(expected_result)
        tm.assert_series_equal(start_series, expected_series)

def test_coercion_with_loc_setitem(self):
    for start_data, expected_result in self.EXPECTED_RESULTS:
        start_series = Series(start_data)
        start_series.loc[0] = None

        expected_series = Series(expected_result)
        tm.assert_series_equal(start_series, expected_series)

def test_coercion_with_setitem_and_series(self):
    for start_data, expected_result in self.EXPECTED_RESULTS:
        start_series = Series(start_data)
        start_series[start_series == start_series[0]] = None

        expected_series = Series(expected_result)
        tm.assert_series_equal(start_series, expected_series)

def test_coercion_with_loc_and_series(self):
    for start_data, expected_result in self.EXPECTED_RESULTS:
        start_series = Series(start_data)
        start_series.loc[start_series == start_series[0]] = None

        expected_series = Series(expected_result)
        tm.assert_series_equal(start_series, expected_series)

def test_coercion_with_loc(self):
    for start_data, expected_result, in self.EXPECTED_SINGLE_ROW_RESULTS:
        start_dataframe = DataFrame({'foo': start_data})
        start_dataframe.loc[0, ['foo']] = None

        expected_dataframe = DataFrame({'foo': expected_result})
        tm.assert_frame_equal(start_dataframe, expected_dataframe)

def test_at_and_iat_get(self):
    def _check(f, func, values=False):

        if f is not None:
            indicies = self.generate_indices(f, values)
            for i in indicies:
                result = getattr(f, func)[i]
                expected = self.get_value(f, i, values)
                tm.assert_almost_equal(result, expected)

    for o in self._objs:

        d = getattr(self, o)

        # iat
        for f in [d['ints'], d['uints']]:
            _check(f, 'iat', values=True)

        for f in [d['labels'], d['ts'], d['floats']]:
            if f is not None:
                pytest.raises(ValueError, self.check_values, f, 'iat')

        # at
        for f in [d['ints'], d['uints'], d['labels'],
                  d['ts'], d['floats']]:
            _check(f, 'at')

def test_at_and_iat_set(self):
    def _check(f, func, values=False):

        if f is not None:
            indicies = self.generate_indices(f, values)
            for i in indicies:
                getattr(f, func)[i] = 1
                expected = self.get_value(f, i, values)
                tm.assert_almost_equal(expected, 1)

    for t in self._objs:

        d = getattr(self, t)

        # iat
        for f in [d['ints'], d['uints']]:
            _check(f, 'iat', values=True)

        for f in [d['labels'], d['ts'], d['floats']]:
            if f is not None:
                pytest.raises(ValueError, _check, f, 'iat')

        # at
        for f in [d['ints'], d['uints'], d['labels'],
                  d['ts'], d['floats']]:
            _check(f, 'at')

def test_css_absolute_font_size(size, relative_to, resolved):
    if relative_to is None:
        inherited = None
    else:
        inherited = {'font-size': relative_to}
    assert_resolves('font-size: {size}'.format(size=size),
                    {'font-size': resolved}, inherited=inherited)

def test_css_relative_font_size(size, relative_to, resolved):
    if relative_to is None:
        inherited = None
    else:
        inherited = {'font-size': relative_to}
    assert_resolves('font-size: {size}'.format(size=size),
                    {'font-size': resolved}, inherited=inherited)

def test_nonetype_top_level_bottom_level(self):
    # GH21158: If inner level json has a key with a null value
    # make sure it doesnt do a new_d.pop twice and except
    data = {
        "id": None,
        "location": {
            "country": {
                "state": {
                    "id": None,
                    "town.info": {
                        "id": None,
                        "region": None,
                        "x": 49.151580810546875,
                        "y": -33.148521423339844,
                        "z": 27.572303771972656}}}
        }
    }
    result = nested_to_record(data)
    expected = {
        'id': None,
        'location.country.state.id': None,
        'location.country.state.town.info.id': None,
        'location.country.state.town.info.region': None,
        'location.country.state.town.info.x': 49.151580810546875,
        'location.country.state.town.info.y': -33.148521423339844,
        'location.country.state.town.info.z': 27.572303771972656}
    assert result == expected

def test_nonetype_multiple_levels(self):
    # GH21158: If inner level json has a key with a null value
    # make sure it doesnt do a new_d.pop twice and except
    data = {
        "id": None,
        "location": {
            "id": None,
            "country": {
                "id": None,
                "state": {
                    "id": None,
                    "town.info": {
                        "region": None,
                        "x": 49.151580810546875,
                        "y": -33.148521423339844,
                        "z": 27.572303771972656}}}
        }
    }
    result = nested_to_record(data)
    expected = {
        'id': None,
        'location.id': None,
        'location.country.id': None,
        'location.country.state.id': None,
        'location.country.state.town.info.region': None,
        'location.country.state.town.info.x': 49.151580810546875,
        'location.country.state.town.info.y': -33.148521423339844,
        'location.country.state.town.info.z': 27.572303771972656}
    assert result == expected

def convert_days(x):
    x = x.strip()
    if not x:
        return np.nan

    is_plus = x.endswith('+')
    if is_plus:
        x = int(x[:-1]) + 1
    else:
        x = int(x)
    return x

def convert_days_sentinel(x):
    x = x.strip()
    if not x:
        return np.nan

    is_plus = x.endswith('+')
    if is_plus:
        x = int(x[:-1]) + 1
    else:
        x = int(x)
    return x

def test_skiprows_bug(self): # see gh-505 text = """#foo,a,b,c #foo,a,b,c #foo,a,b,c #foo,a,b,c #foo,a,b,c #foo,a,b,c 1/1/2000,1.,2.,3. 1/2/2000,4,5,6 1/3/2000,7,8,9 """ data = self.read_csv(StringIO(text), skiprows=lrange(6), header=None, index_col=0, parse_dates=True)
data2 = self.read_csv(StringIO(text), skiprows=6, header=None, index_col=0, parse_dates=True)
expected = DataFrame(np.arange(1., 10.).reshape((3, 3)), columns=[1, 2, 3], index=[datetime(2000, 1, 1), datetime(2000, 1, 2), datetime(2000, 1, 3)]) expected.index.name = 0 tm.assert_frame_equal(data, expected) tm.assert_frame_equal(data, data2)

def test_usecols_with_unicode_strings(self):
    # see gh-13219

    s = '''AAA,BBB,CCC,DDD
    0.056674973,8,True,a
    2.613230982,2,False,b
    3.568935038,7,False,a
    '''

    data = {
        'AAA': {
            0: 0.056674972999999997,
            1: 2.6132309819999997,
            2: 3.5689350380000002
        },
        'BBB': {0: 8, 1: 2, 2: 7}
    }
    expected = DataFrame(data)

    df = self.read_csv(StringIO(s), usecols=[u'AAA', u'BBB'])
    tm.assert_frame_equal(df, expected)

def test_usecols_with_single_byte_unicode_strings(self):
    # see gh-13219

    s = '''A,B,C,D
    0.056674973,8,True,a
    2.613230982,2,False,b
    3.568935038,7,False,a
    '''

    data = {
        'A': {
            0: 0.056674972999999997,
            1: 2.6132309819999997,
            2: 3.5689350380000002
        },
        'B': {0: 8, 1: 2, 2: 7}
    }
    expected = DataFrame(data)

    df = self.read_csv(StringIO(s), usecols=[u'A', u'B'])
    tm.assert_frame_equal(df, expected)

def test_usecols_with_multibyte_characters(self):
    s = ''',,,
    0.056674973,8,True,a
    2.613230982,2,False,b
    3.568935038,7,False,a
    '''
    data = {
        '': {
            0: 0.056674972999999997,
            1: 2.6132309819999997,
            2: 3.5689350380000002
        },
        '': {0: 8, 1: 2, 2: 7}
    }
    expected = DataFrame(data)

    df = self.read_csv(StringIO(s), usecols=['', ''])
    tm.assert_frame_equal(df, expected)

