def test_finder_quarterly(self): yrs = [3.5, 11]
if self.mpl_ge_2_0_0: xpl1 = [68, 68] xpl2 = [72, 68] else: xpl1 = xpl2 = [Period('1988Q1').ordinal] * len(yrs)
for i, n in enumerate(yrs): xp = xpl1[i] rng = period_range('1987Q2', periods=int(n * 4), freq='Q') ser = Series(np.random.randn(len(rng)), rng) _, ax = self.plt.subplots() ser.plot(ax=ax) xaxis = ax.get_xaxis() rs = xaxis.get_majorticklocs()[0] assert rs == xp xp = xpl2[i] (vmin, vmax) = ax.get_xlim() ax.set_xlim(vmin + 0.9, vmax) rs = xaxis.get_majorticklocs()[0] assert xp == rs self.plt.close(ax.get_figure())

def test_finder_monthly(self): yrs = [1.15, 2.5, 4, 11]
if self.mpl_ge_2_0_0: xpl1 = [216, 216, 204, 204] xpl2 = [216, 216, 216, 204] else: xpl1 = xpl2 = [Period('Jan 1988').ordinal] * len(yrs)
for i, n in enumerate(yrs): xp = xpl1[i] rng = period_range('1987Q2', periods=int(n * 12), freq='M') ser = Series(np.random.randn(len(rng)), rng) _, ax = self.plt.subplots() ser.plot(ax=ax) xaxis = ax.get_xaxis() rs = xaxis.get_majorticklocs()[0] assert rs == xp xp = xpl2[i] vmin, vmax = ax.get_xlim() ax.set_xlim(vmin + 0.9, vmax) rs = xaxis.get_majorticklocs()[0] assert xp == rs self.plt.close(ax.get_figure())

def test_secondary_y_non_ts_xlim(self): # GH 3490 - non-timeseries with secondary y index_1 = [1, 2, 3, 4] index_2 = [5, 6, 7, 8] s1 = Series(1, index=index_1) s2 = Series(2, index=index_2)
_, ax = self.plt.subplots() s1.plot(ax=ax) left_before, right_before = ax.get_xlim() s2.plot(secondary_y=True, ax=ax) left_after, right_after = ax.get_xlim()
assert left_before >= left_after assert right_before < right_after

def test_secondary_y_regular_ts_xlim(self): # GH 3490 - regular-timeseries with secondary y index_1 = date_range(start='2000-01-01', periods=4, freq='D') index_2 = date_range(start='2000-01-05', periods=4, freq='D') s1 = Series(1, index=index_1) s2 = Series(2, index=index_2)
_, ax = self.plt.subplots() s1.plot(ax=ax) left_before, right_before = ax.get_xlim() s2.plot(secondary_y=True, ax=ax) left_after, right_after = ax.get_xlim()
assert left_before >= left_after assert right_before < right_after

def test_all_invalid_plot_data(self):
    df = DataFrame(list('abcd'))
    for kind in plotting._core._common_kinds:
        if not _ok_for_gaussian_kde(kind):
            continue
        with pytest.raises(TypeError):
            df.plot(kind=kind)

def test_invalid_plot_data(self):
    s = Series(list('abcd'))
    _, ax = self.plt.subplots()
    for kind in plotting._core._common_kinds:
        if not _ok_for_gaussian_kde(kind):
            continue
        with pytest.raises(TypeError):
            s.plot(kind=kind, ax=ax)

def test_partially_invalid_plot_data(self):
    s = Series(['a', 'b', 1.0, 2])
    _, ax = self.plt.subplots()
    for kind in plotting._core._common_kinds:
        if not _ok_for_gaussian_kde(kind):
            continue
        with pytest.raises(TypeError):
            s.plot(kind=kind, ax=ax)

def test_basic(self):
expected = self.asof trades = self.trades quotes = self.quotes
result = merge_asof(trades, quotes, on='time', by='ticker') assert_frame_equal(result, expected)

def test_basic_left_by_right_by(self):
# GH14253 expected = self.asof trades = self.trades quotes = self.quotes
result = merge_asof(trades, quotes, on='time', left_by='ticker', right_by='ticker') assert_frame_equal(result, expected)

def test_multi_index(self):
# MultiIndex is prohibited trades = self.trades.set_index(['time', 'price']) quotes = self.quotes.set_index('time') with pytest.raises(MergeError): merge_asof(trades, quotes, left_index=True, right_index=True)
trades = self.trades.set_index('time') quotes = self.quotes.set_index(['time', 'bid']) with pytest.raises(MergeError): merge_asof(trades, quotes, left_index=True, right_index=True)

def test_on_and_index(self):
# 'on' parameter and index together is prohibited trades = self.trades.set_index('time') quotes = self.quotes.set_index('time') with pytest.raises(MergeError): merge_asof(trades, quotes, left_on='price', left_index=True, right_index=True)
trades = self.trades.set_index('time') quotes = self.quotes.set_index('time') with pytest.raises(MergeError): merge_asof(trades, quotes, right_on='bid', left_index=True, right_index=True)

def test_cant_compare_tz_naive_w_aware(self): # see gh-1404 a = Timestamp('3/12/2012') b = Timestamp('3/12/2012', tz='utc')
pytest.raises(Exception, a.__eq__, b) pytest.raises(Exception, a.__ne__, b) pytest.raises(Exception, a.__lt__, b) pytest.raises(Exception, a.__gt__, b) pytest.raises(Exception, b.__eq__, a) pytest.raises(Exception, b.__ne__, a) pytest.raises(Exception, b.__lt__, a) pytest.raises(Exception, b.__gt__, a)
if PY2: pytest.raises(Exception, a.__eq__, b.to_pydatetime()) pytest.raises(Exception, a.to_pydatetime().__eq__, b) else: assert not a == b.to_pydatetime() assert not a.to_pydatetime() == b

def test_cant_compare_tz_naive_w_aware_explicit_pytz(self): # see gh-1404 a = Timestamp('3/12/2012') b = Timestamp('3/12/2012', tz=utc)
pytest.raises(Exception, a.__eq__, b) pytest.raises(Exception, a.__ne__, b) pytest.raises(Exception, a.__lt__, b) pytest.raises(Exception, a.__gt__, b) pytest.raises(Exception, b.__eq__, a) pytest.raises(Exception, b.__ne__, a) pytest.raises(Exception, b.__lt__, a) pytest.raises(Exception, b.__gt__, a)
if PY2: pytest.raises(Exception, a.__eq__, b.to_pydatetime()) pytest.raises(Exception, a.to_pydatetime().__eq__, b) else: assert not a == b.to_pydatetime() assert not a.to_pydatetime() == b

def test_cant_compare_tz_naive_w_aware_dateutil(self): # see gh-1404 a = Timestamp('3/12/2012') b = Timestamp('3/12/2012', tz=tzutc())
pytest.raises(Exception, a.__eq__, b) pytest.raises(Exception, a.__ne__, b) pytest.raises(Exception, a.__lt__, b) pytest.raises(Exception, a.__gt__, b) pytest.raises(Exception, b.__eq__, a) pytest.raises(Exception, b.__ne__, a) pytest.raises(Exception, b.__lt__, a) pytest.raises(Exception, b.__gt__, a)
if PY2: pytest.raises(Exception, a.__eq__, b.to_pydatetime()) pytest.raises(Exception, a.to_pydatetime().__eq__, b) else: assert not a == b.to_pydatetime() assert not a.to_pydatetime() == b

def test_class_ops_pytz(self): def compare(x, y): assert (int(Timestamp(x).value / 1e9) == int(Timestamp(y).value / 1e9))
compare(Timestamp.now(), datetime.now()) compare(Timestamp.now('UTC'), datetime.now(timezone('UTC'))) compare(Timestamp.utcnow(), datetime.utcnow()) compare(Timestamp.today(), datetime.today()) current_time = calendar.timegm(datetime.now().utctimetuple()) compare(Timestamp.utcfromtimestamp(current_time), datetime.utcfromtimestamp(current_time)) compare(Timestamp.fromtimestamp(current_time), datetime.fromtimestamp(current_time))
date_component = datetime.utcnow() time_component = (date_component + timedelta(minutes=10)).time() compare(Timestamp.combine(date_component, time_component), datetime.combine(date_component, time_component))

def test_class_ops_dateutil(self): def compare(x, y): assert (int(np.round(Timestamp(x).value / 1e9)) == int(np.round(Timestamp(y).value / 1e9)))
compare(Timestamp.now(), datetime.now()) compare(Timestamp.now('UTC'), datetime.now(tzutc())) compare(Timestamp.utcnow(), datetime.utcnow()) compare(Timestamp.today(), datetime.today()) current_time = calendar.timegm(datetime.now().utctimetuple()) compare(Timestamp.utcfromtimestamp(current_time), datetime.utcfromtimestamp(current_time)) compare(Timestamp.fromtimestamp(current_time), datetime.fromtimestamp(current_time))
date_component = datetime.utcnow() time_component = (date_component + timedelta(minutes=10)).time() compare(Timestamp.combine(date_component, time_component), datetime.combine(date_component, time_component))

def test_interpolate_akima(self): _skip_if_no_akima()
ser = Series([10, 11, 12, 13])
expected = Series([11.00, 11.25, 11.50, 11.75, 12.00, 12.25, 12.50, 12.75, 13.00], index=Index([1.0, 1.25, 1.5, 1.75, 2.0, 2.25, 2.5, 2.75, 3.0])) # interpolate at new_index new_index = ser.index.union(Index([1.25, 1.5, 1.75, 2.25, 2.5, 2.75])) interp_s = ser.reindex(new_index).interpolate(method='akima') assert_series_equal(interp_s[1:3], expected)

def test_concat(self): val1 = np.array([1, 2, np.nan, np.nan, 0, np.nan]) val2 = np.array([3, np.nan, 4, 0, 0])
for kind in ['integer', 'block']: sparse1 = pd.SparseSeries(val1, name='x', kind=kind) sparse2 = pd.SparseSeries(val2, name='y', kind=kind)
res = pd.concat([sparse1, sparse2]) exp = pd.concat([pd.Series(val1), pd.Series(val2)]) exp = pd.SparseSeries(exp, kind=kind) tm.assert_sp_series_equal(res, exp)
sparse1 = pd.SparseSeries(val1, fill_value=0, name='x', kind=kind) sparse2 = pd.SparseSeries(val2, fill_value=0, name='y', kind=kind)
res = pd.concat([sparse1, sparse2]) exp = pd.concat([pd.Series(val1), pd.Series(val2)]) exp = pd.SparseSeries(exp, fill_value=0, kind=kind) tm.assert_sp_series_equal(res, exp)

