    assert repr(self.offset) == ''
    assert repr(self.offset2) == '<2 * CustomBusinessDays>'

    if compat.PY37:
        expected = ''
    else:
        expected = ''
    assert repr(self.offset + timedelta(1)) == expected

def test_bad_arg_length_max_value_single(self): args = (None, None) compat_args = ('foo',)
min_fname_arg_count = 0 max_length = len(compat_args) + min_fname_arg_count actual_length = len(args) + min_fname_arg_count msg = (r"{fname}\(\) takes at most {max_length} " r"argument \({actual_length} given\)" .format(fname=self.fname, max_length=max_length, actual_length=actual_length))
with tm.assert_raises_regex(TypeError, msg): validate_args(self.fname, args, min_fname_arg_count, compat_args)

def test_invalid_total_length_max_length_one(self): compat_args = ('foo',) kwargs = {'foo': 'FOO'} args = ('FoO', 'BaZ')
min_fname_arg_count = 0 max_length = len(compat_args) + min_fname_arg_count actual_length = len(kwargs) + len(args) + min_fname_arg_count msg = (r"{fname}\(\) takes at most {max_length} " r"argument \({actual_length} given\)" .format(fname=self.fname, max_length=max_length, actual_length=actual_length))
with tm.assert_raises_regex(TypeError, msg): validate_args_and_kwargs(self.fname, args, kwargs, min_fname_arg_count, compat_args)

def test_bad_arg_length_max_value_multiple(self): args = (None, None) compat_args = dict(foo=None)
min_fname_arg_count = 2 max_length = len(compat_args) + min_fname_arg_count actual_length = len(args) + min_fname_arg_count msg = (r"{fname}\(\) takes at most {max_length} " r"arguments \({actual_length} given\)" .format(fname=self.fname, max_length=max_length, actual_length=actual_length))
with tm.assert_raises_regex(TypeError, msg): validate_args(self.fname, args, min_fname_arg_count, compat_args)

def test_invalid_total_length_max_length_multiple(self): compat_args = ('foo', 'bar', 'baz') kwargs = {'foo': 'FOO', 'bar': 'BAR'} args = ('FoO', 'BaZ')
min_fname_arg_count = 2 max_length = len(compat_args) + min_fname_arg_count actual_length = len(kwargs) + len(args) + min_fname_arg_count msg = (r"{fname}\(\) takes at most {max_length} " r"arguments \({actual_length} given\)" .format(fname=self.fname, max_length=max_length, actual_length=actual_length))
with tm.assert_raises_regex(TypeError, msg): validate_args_and_kwargs(self.fname, args, kwargs, min_fname_arg_count, compat_args)

def _skip_if_mpl_1_5():
    mod = safe_import("matplotlib")

    if mod:
        v = mod.__version__
        if LooseVersion(v) > LooseVersion('1.4.3') or str(v)[0] == '0':
            return True
        else:
            mod.use("Agg", warn=False)

def _skip_if_mpl_2_2():
    mod = safe_import("matplotlib")

    if mod:
        v = mod.__version__
        if LooseVersion(v) > LooseVersion('2.1.2'):
            return True
        else:
            mod.use("Agg", warn=False)

def start_blank_lines(self):
    i = None
    if self.raw_doc:
        for i, row in enumerate(self.raw_doc.split('\n')):
            if row.strip():
                break
    return i

def end_blank_lines(self):
    i = None
    if self.raw_doc:
        for i, row in enumerate(reversed(self.raw_doc.split('\n'))):
            if row.strip():
                break
    return i
